mapper.update:
update teacher


        name = #{name} ,


        phone = #{phone},


        address = #{address}


where t_code = #{code};

mapper.select:
select *
from teacher a
where tea_id = (select id from teach_user b where b.id = #{id});

select d.s_code code, course, grade, d.sex sex, dept.name dept, d.name name, collage.name collage
from student d
join
(select b.stu_id code, c.c_name course, b.grade grade
from (select id, cou_id from teach_cou where id = #{teaId}) a
join stu_grade b on b.teach_cou_id = a.id
join course c on c.id = a.cou_id) f on d.id = f.code
join dept on dept.id = d.id
join collage on dept.col_id = collage.id


        d.name = #{s.name}


        and sex = #{s.sex}


        and dept.name = #{s.dept}


        and d.s_code = #{s.code}


;
